mapper.select:
SELECT DISTINCT tm.*
FROM tb_menu tm
LEFT JOIN tb_role_menu trm ON tm.id = trm.menu_id
LEFT JOIN tb_user_role tur ON trm.role_id = tur.role_id
WHERE tur.user_id = #{id}
AND tm.type != 3;

SELECT *
FROM tb_menu tm
WHERE tm.parent_id = #{pid}
AND tm.type != 3;

SELECT *
FROM tb_menu t
WHERE t.`name` = #{name}
OR t.component_name = #{componentName}
LIMIT 1;
